import React, {useState} from 'react';
import {
  View,
  Text,
  StyleSheet,
  TextInput,
  TouchableOpacity,
  Alert,
  ScrollView,
  KeyboardAvoidingView,
  Platform,
} from 'react-native';

const AddProductScreen = ({navigation}: any) => {
  const [name, setName] = useState('');
  const [description, setDescription] = useState('');
  const [price, setPrice] = useState('');
  const [brand, setBrand] = useState('');
  const [category, setCategory] = useState('');
  const [sizes, setSizes] = useState('');
  const [isLoading, setIsLoading] = useState(false);

  const categories = [
    'Zapatos',
    'Ropa',
    'Accesorios',
    'Calcetines',
    'Otros',
  ];

  const validateForm = () => {
    if (!name.trim()) {
      Alert.alert('Error', 'Por favor ingresa el nombre del producto');
      return false;
    }

    if (!description.trim()) {
      Alert.alert('Error', 'Por favor ingresa la descripción del producto');
      return false;
    }

    if (!price.trim()) {
      Alert.alert('Error', 'Por favor ingresa el precio del producto');
      return false;
    }

    const priceNumber = parseFloat(price);
    if (isNaN(priceNumber) || priceNumber <= 0) {
      Alert.alert('Error', 'Por favor ingresa un precio válido');
      return false;
    }

    if (!brand.trim()) {
      Alert.alert('Error', 'Por favor ingresa la marca del producto');
      return false;
    }

    if (!category) {
      Alert.alert('Error', 'Por favor selecciona una categoría');
      return false;
    }

    return true;
  };

  const handleAddProduct = async () => {
    if (!validateForm()) {
      return;
    }

    setIsLoading(true);

    try {
      // Simular guardado del producto (en una app real usarías Firebase)
      const productData = {
        id: Date.now().toString(),
        name: name.trim(),
        description: description.trim(),
        price: parseFloat(price),
        brand: brand.trim(),
        category: category,
        sizes: sizes.trim() ? sizes.split(',').map(s => s.trim()) : [],
        imageUrl: 'https://via.placeholder.com/300x300?text=Producto',
        createdAt: new Date().toISOString(),
        isNew: true,
        isRecommended: false,
        isPromotional: false,
      };

      // Aquí guardarías en Firebase
      console.log('Producto a guardar:', productData);

      Alert.alert(
        'Producto Agregado',
        'El producto ha sido agregado exitosamente',
        [
          {
            text: 'OK',
            onPress: () => {
              // Limpiar formulario
              setName('');
              setDescription('');
              setPrice('');
              setBrand('');
              setCategory('');
              setSizes('');
              navigation.goBack();
            },
          },
        ]
      );
    } catch (error) {
      console.error('Error adding product:', error);
      Alert.alert('Error', 'No se pudo agregar el producto. Inténtalo de nuevo.');
    } finally {
      setIsLoading(false);
    }
  };

  const selectCategory = (selectedCategory: string) => {
    setCategory(selectedCategory);
  };

  return (
    <KeyboardAvoidingView
      style={styles.container}
      behavior={Platform.OS === 'ios' ? 'padding' : 'height'}>
      <ScrollView contentContainerStyle={styles.scrollContainer}>
        <View style={styles.header}>
          <TouchableOpacity
            style={styles.backButton}
            onPress={() => navigation.goBack()}>
            <Text style={styles.backButtonText}>← Volver</Text>
          </TouchableOpacity>
          <Text style={styles.headerTitle}>Agregar Producto</Text>
        </View>

        <View style={styles.form}>
          <View style={styles.inputContainer}>
            <Text style={styles.label}>Nombre del Producto *</Text>
            <TextInput
              style={styles.input}
              placeholder="Ej: Zapatos Deportivos Nike"
              value={name}
              onChangeText={setName}
            />
          </View>

          <View style={styles.inputContainer}>
            <Text style={styles.label}>Descripción *</Text>
            <TextInput
              style={[styles.input, styles.textArea]}
              placeholder="Describe el producto..."
              value={description}
              onChangeText={setDescription}
              multiline
              numberOfLines={4}
            />
          </View>

          <View style={styles.rowContainer}>
            <View style={[styles.inputContainer, styles.halfWidth]}>
              <Text style={styles.label}>Precio (€) *</Text>
              <TextInput
                style={styles.input}
                placeholder="0.00"
                value={price}
                onChangeText={setPrice}
                keyboardType="numeric"
              />
            </View>

            <View style={[styles.inputContainer, styles.halfWidth]}>
              <Text style={styles.label}>Marca *</Text>
              <TextInput
                style={styles.input}
                placeholder="Ej: Nike, Adidas..."
                value={brand}
                onChangeText={setBrand}
              />
            </View>
          </View>

          <View style={styles.inputContainer}>
            <Text style={styles.label}>Categoría *</Text>
            <View style={styles.categoriesContainer}>
              {categories.map(cat => (
                <TouchableOpacity
                  key={cat}
                  style={[
                    styles.categoryButton,
                    category === cat && styles.selectedCategoryButton,
                  ]}
                  onPress={() => selectCategory(cat)}>
                  <Text
                    style={[
                      styles.categoryButtonText,
                      category === cat && styles.selectedCategoryButtonText,
                    ]}>
                    {cat}
                  </Text>
                </TouchableOpacity>
              ))}
            </View>
          </View>

          <View style={styles.inputContainer}>
            <Text style={styles.label}>Tallas Disponibles</Text>
            <TextInput
              style={styles.input}
              placeholder="Ej: S, M, L, XL (separadas por comas)"
              value={sizes}
              onChangeText={setSizes}
            />
            <Text style={styles.helpText}>
              Separa las tallas con comas (ej: S, M, L, XL)
            </Text>
          </View>

          <TouchableOpacity
            style={[styles.addButton, isLoading && styles.disabledButton]}
            onPress={handleAddProduct}
            disabled={isLoading}>
            <Text style={styles.addButtonText}>
              {isLoading ? 'Agregando...' : 'Agregar Producto'}
            </Text>
          </TouchableOpacity>

          <View style={styles.requiredNote}>
            <Text style={styles.requiredText}>* Campos obligatorios</Text>
          </View>
        </View>
      </ScrollView>
    </KeyboardAvoidingView>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#f8f9fa',
  },
  scrollContainer: {
    flexGrow: 1,
  },
  header: {
    backgroundColor: '#000',
    padding: 20,
    paddingTop: 50,
    flexDirection: 'row',
    alignItems: 'center',
  },
  backButton: {
    marginRight: 15,
  },
  backButtonText: {
    color: '#fff',
    fontSize: 16,
    fontWeight: 'bold',
  },
  headerTitle: {
    fontSize: 20,
    fontWeight: 'bold',
    color: '#fff',
    flex: 1,
  },
  form: {
    backgroundColor: '#fff',
    margin: 20,
    padding: 20,
    borderRadius: 15,
    shadowColor: '#000',
    shadowOffset: {width: 0, height: 2},
    shadowOpacity: 0.1,
    shadowRadius: 4,
    elevation: 3,
  },
  inputContainer: {
    marginBottom: 20,
  },
  rowContainer: {
    flexDirection: 'row',
    justifyContent: 'space-between',
  },
  halfWidth: {
    width: '48%',
  },
  label: {
    fontSize: 16,
    fontWeight: 'bold',
    color: '#000',
    marginBottom: 8,
  },
  input: {
    borderWidth: 2,
    borderColor: '#eee',
    borderRadius: 10,
    padding: 15,
    fontSize: 16,
  },
  textArea: {
    height: 100,
    textAlignVertical: 'top',
  },
  categoriesContainer: {
    flexDirection: 'row',
    flexWrap: 'wrap',
    marginTop: 10,
  },
  categoryButton: {
    backgroundColor: '#f8f9fa',
    paddingHorizontal: 15,
    paddingVertical: 10,
    borderRadius: 20,
    marginRight: 10,
    marginBottom: 10,
    borderWidth: 2,
    borderColor: '#eee',
  },
  selectedCategoryButton: {
    backgroundColor: '#000',
    borderColor: '#000',
  },
  categoryButtonText: {
    fontSize: 14,
    fontWeight: 'bold',
    color: '#666',
  },
  selectedCategoryButtonText: {
    color: '#fff',
  },
  helpText: {
    fontSize: 12,
    color: '#666',
    marginTop: 5,
    fontStyle: 'italic',
  },
  addButton: {
    backgroundColor: '#000',
    paddingVertical: 15,
    borderRadius: 10,
    alignItems: 'center',
    marginTop: 20,
  },
  disabledButton: {
    backgroundColor: '#ccc',
  },
  addButtonText: {
    color: '#fff',
    fontSize: 16,
    fontWeight: 'bold',
  },
  requiredNote: {
    marginTop: 15,
    alignItems: 'center',
  },
  requiredText: {
    fontSize: 12,
    color: '#666',
    fontStyle: 'italic',
  },
});

export default AddProductScreen;
